(129, 1, '2013-10-08 17:53:48', '2013-10-08 08:53:48', '[row customclass="align-center"][columns span="12" customclass=" add-top pad-top"][section customclass="fx fx-rotateIn"]<img class="aligncenter size-full wp-image-10" alt="logo" src="http://globalit-academy.localhost/wp-content/uploads/2013/09/logo.png" width="180" height="180" />[/section][/columns][/row][row customclass="align-center"][columns span="12" customclass=""][section customclass="fx fx-fadeInUpBig"][large_text customclass=""]It''s Simple[/large_text][/section][/columns][/row][row customclass="add-bottom-extra"][columns span="12" customclass="align-center"][subheading customclass="light fx fx-fadeInDownBig"]An Easy to Use, One Page Creative Theme[/subheading][/columns][/row][row customclass="hidden-phone hidden-tablet"][columns span="12" customclass="align-center"][section customclass="fx fx-fadeInDownBig"][sticky_button url="#about"]<img class="aligncenter size-full wp-image-11" alt="scroll" src="http://globalit-academy.localhost/wp-content/uploads/2013/09/scroll.png" width="68" height="68" />[/sticky_button][/section][/columns][/row]', 'Home', '', 'inherit', 'closed', 'closed', '', '6-revision-v1', '', '', '2013-10-08 17:53:48', '2013-10-08 08:53:48', '', 6, 'http://globalit-academy.localhost/?p=129', 0, 'revision', '', 0),
